		merge sf.ConfigParam target
		using
		(
			select
				x.ConfigParamSCD
			 ,x.ConfigParamName
			 ,x.DefaultParamValue
			 ,x.DataType
			 ,x.MaxLength
			 ,x.IsReadOnly
			 ,x.UsageNotes
			 ,@SetupUser CreateUser
			 ,@SetupUser UpdateUser
			from
				@setup x
		) source
		on target.ConfigParamSCD = source.ConfigParamSCD
		when not matched by target then
			insert
			(
				ConfigParamSCD
			 ,ConfigParamName
			 ,DefaultParamValue
			 ,DataType
			 ,MaxLength
			 ,IsReadOnly
			 ,UsageNotes
			 ,CreateUser
			 ,UpdateUser
			)
			values
			(
				source.ConfigParamSCD
			 ,source.ConfigParamName
			 ,source.DefaultParamValue
			 ,source.DataType
			 ,source.MaxLength
			 ,source.IsReadOnly
			 ,source.UsageNotes
			 ,@SetupUser
			 ,@SetupUser
			)
		when matched then update set
												ConfigParamName = source.ConfigParamName
											 ,DefaultParamValue = source.DefaultParamValue
											 ,DataType = source.DataType
											 ,MaxLength = source.MaxLength
											 ,IsReadOnly = source.IsReadOnly
											 ,UsageNotes = source.UsageNotes
											 ,UpdateUser = @SetupUser
											 ,ParamValue = (case when source.IsReadOnly = 1 and right(source.ConfigParamSCD, 2) <> 'No' then null else ParamValue end)	-- set override value to NULL if parameter is read-only!
		when not matched by source then delete;

		-- check count of @setup table and the target table
		-- target should have at least as many rows as @setup

		select	@sourceCount = count(1) from	@setup;
		select	@targetCount = count(1) from	sf.ConfigParam;

		if isnull(@targetCount, 0) < @sourceCount
		begin

			exec sf.pMessage#Get
				@MessageSCD = 'SetupCountTooLow'
			 ,@MessageText = @errorText output
			 ,@DefaultText = N'Insert of some setup records failed. Source table count is %1 but target table (%2) count is only %3. Check "JOIN" conditions.'
			 ,@Arg1 = @sourceCount
			 ,@Arg2 = 'sf.ConfigParam'
			 ,@Arg3 = @targetCount;

			raiserror(@errorText, 18, 1);
		end;
	end try

	begin catch
		exec @errorNo = sf.pErrorRethrow;
	end catch;

	return (@errorNo);

end;
