  ,(select cast( sf.fConfigParam#Value('MNU.AD.RepostGL')           as bit))                    MNU_AD_RepostGL
  , cast( 'This value determines whether or not the repost GL menu item is enabled.' as nvarchar(max)) MNU_AD_RepostGLUsage
  ,(select cast( sf.fConfigParam#Value('MNU.AD.Settings')           as bit))                    MNU_AD_Settings
