  ,(select cast( sf.fConfigParam#Value('PaymentBlockedByPAPText')   as nvarchar(999)))          PaymentBlockedByPAPText
  , cast( 'This text is shown on the invoice payment page when payment is blocked by a pre-authorized payment.' as nvarchar(max)) PaymentBlockedByPAPTextUsage
