  ,(select cast( sf.fConfigParam#Value('PER.MailingAddress')        as bit))                    PER_MailingAddress
  , cast( 'This value determines whether or not mailing addresses are enabled on person details.' as nvarchar(max)) PER_MailingAddressUsage
  ,(select cast( sf.fConfigParam#Value('PER.MailingPreference')     as bit))                    PER_MailingPreference
  , cast( 'This value determines whether or not mailing preferences are enabled on person details.' as nvarchar(max)) PER_MailingPreferenceUsage
  ,(select cast( sf.fConfigParam#Value('PER.OrgContact')            as bit))                    PER_OrgContact
  , cast( 'This value determines whether or not org contacts are enabled on person details.' as nvarchar(max)) PER_OrgContactUsage
