  , cast( 'This value determines whether or not practice restriction is enabled on person details.' as nvarchar(max)) PER_PracticeRestrictionUsage
  ,(select cast( sf.fConfigParam#Value('PER.ProfileUpdate')         as bit))                    PER_ProfileUpdate
