  ,(select cast( sf.fConfigParam#Value('PER.RegistrantCredential')  as bit))                    PER_RegistrantCredential
  , cast( 'This value determines whether or not registrant credentials are enabled on person details.' as nvarchar(max)) PER_RegistrantCredentialUsage
  ,(select cast( sf.fConfigParam#Value('PER.RegistrantEmployment')  as bit))                    PER_RegistrantEmployment
  , cast( 'This value determines whether or not registrant employments are enabled on person details.' as nvarchar(max)) PER_RegistrantEmploymentUsage
  ,(select cast( sf.fConfigParam#Value('PER.RegistrantLanguage')    as bit))                    PER_RegistrantLanguage
  , cast( 'This value determines whether or not registrant language is enabled on person details.' as nvarchar(max)) PER_RegistrantLanguageUsage
  ,(select cast( sf.fConfigParam#Value('PER.RegistrantPractice')    as bit))                    PER_RegistrantPractice
  , cast( 'This value determines whether or not registrant practice is enabled on person details.' as nvarchar(max)) PER_RegistrantPracticeUsage
  ,(select cast( sf.fConfigParam#Value('PER.Renewals')              as bit))                    PER_Renewals
  , cast( 'This value determines whether or not renewals are enabled on person details.' as nvarchar(max)) PER_RenewalsUsage
  ,(select cast( sf.fConfigParam#Value('PER.ReviewApplication')     as bit))                    PER_ReviewApplication
  , cast( 'This value determines whether or not application reviews are enabled on person details.' as nvarchar(max)) PER_ReviewApplicationUsage
  ,(select cast( sf.fConfigParam#Value('PER.ReviewAudit')           as bit))                    PER_ReviewAudit
  , cast( 'This value determines whether or not audit reviews are enabled on person details.' as nvarchar(max)) PER_ReviewAuditUsage
  ,(select cast( sf.fConfigParam#Value('PolicyHelpDeskNumber')      as nvarchar(50)))           PolicyHelpDeskNumber
  , cast( 'The policy help desk number is displayed in the application to give users a number to call when they have policy issues.' as nvarchar(max)) PolicyHelpDeskNumberUsage
  ,(select cast( sf.fConfigParam#Value('PracticeHourInterval')      as int))                    PracticeHourInterval
  , cast( 'This value is how many years your practice hour requirement is based on (eg: if you require 1000 hours every 5 years, you would put 5 as this value). This assumes your practice hour requirement is rolling, if the practice hour requirement is tied to the active CCP cycle then set this value to -1.' as nvarchar(max)) PracticeHourIntervalUsage
  ,(select cast( sf.fConfigParam#Value('PracticeHourRequirement')   as int))                    PracticeHourRequirement
  , cast( 'This value is how many practice hours your practice hour requirement is based on (eg: if you require 1000 hours every 5 years, you would put 1000 as this value).' as nvarchar(max)) PracticeHourRequirementUsage
  ,(select cast( sf.fConfigParam#Value('ProgramErrorSuffix')        as nvarchar(250)))          ProgramErrorSuffix
  , cast( 'This value is appended onto messages where an unexpected error has occurred in the program but the application is able to recover and keep running. The text should advise the user what to do in this scenario. Normally this will include retrying the process and contacting the help desk if the error persists. While product help desk support is generally provided to licensed administrators only, program errors can be reported by any user.  The option to report the problem with the local administrator may also be helpful. Note that a separate suffix is available for "configuration" errors.' as nvarchar(max)) ProgramErrorSuffixUsage
  ,(select cast( sf.fConfigParam#Value('PUB.DIR.RegNoEnabled')      as bit))                    PUB_DIR_RegNoEnabled
  , cast( 'This value controls whether or not search by registrant no is allowed on the public directory.' as nvarchar(max)) PUB_DIR_RegNoEnabledUsage
  ,(select cast( sf.fConfigParam#Value('PublicDirectoryFilters')    as varchar(3)))             PublicDirectoryFilters
  , cast( 'This value is used to configure which filters are available on the public directory. The filter types must be separated by a comma and is limited to only the filters configured in the product (eg: "PRACTICEREGISTER,EFFECTIVETIME"). Contact Softworks support for assistance when modifying this value.' as nvarchar(max)) PublicDirectoryFiltersUsage
  ,(select cast( sf.fConfigParam#Value('PublicDirectoryRowLimit')   as int))                    PublicDirectoryRowLimit
  , cast( 'This value controls the maximum number of records the application will return when a search is executed on the Public Directory.  The limit is applied on Employer and Member directory features if configured.  Limiting search results is good practice to discourage inappropriate use of your data by individuals who might seek to download large lists of member information to use for direct marketing or even to apply in identity theft schemes.' as nvarchar(max)) PublicDirectoryRowLimitUsage
  ,(select cast( sf.fConfigParam#Value('PublicRegistryConfigured')  as bit))                    PublicRegistryConfigured
  , cast( 'This controls whether or not the public registry is available in the client portal.' as nvarchar(max)) PublicRegistryConfiguredUsage
  ,(select cast( sf.fConfigParam#Value('RecentAccessHours')         as smallint))               RecentAccessHours
  , cast( 'This value established the threshold the application uses for "Recently Accessed" searches.  Many screens in the application provide a quick search where the application returns a list of records that have been recently accessed by the user.  The definition of "recent" is defined using this parameter in units of hours.  The default value is 24 hours; meaning that any record where details have been retrieved by that user within the last 24 hours will be included in "recently accessed" search results. Note that, by default, weekends are not included in the 24 period but this can be controlled using the "Exclude Weekends for Recent Access" configuration option.' as nvarchar(max)) RecentAccessHoursUsage
  ,(select cast( sf.fConfigParam#Value('RegistrantNoMinimum')       as int))                    RegistrantNoMinimum
  , cast( 'The lowest number that should be generated for new registrants. This values sets the starting point for new registration numbers to assign.  The value is NOT updated once registration numbers start being assigned. The number range must be distinct from converted registration numbers and new Application numbers if using both Application and Registration number sequences. Contact the Help Desk for assistance with setting this value.' as nvarchar(max)) RegistrantNoMinimumUsage
  ,(select cast( sf.fConfigParam#Value('RegistrantNoTemplate')      as varchar(50)))            RegistrantNoTemplate
  , cast( 'A template controlling the format of the registrant number. Example: "R####" results in R1001, R1002, etc. Any letter prefix or suffix may be used with “#” indicating number of digits. The minimum number of digits is 4. To avoid assigning this number until applicants have achieved their first Active-Practice registration, you must also define an Applicant# Template. Contact the Help Desk for assistance with setting this value.' as nvarchar(max)) RegistrantNoTemplateUsage
  ,(select cast( sf.fConfigParam#Value('SenderEmail')               as nvarchar(150)))          SenderEmail
  , cast( 'This is the email address used by the system to send email to users and administrators. The email address must bevalid and verified to use the distribution method is configured in email setup:  Amazon SES or SMTP. The accountneed not accept replies.  End users see this address when reading mail messages sent by the application.  If adifferent reply-to address is not specified in email setup, this will be the reply address by default.' as nvarchar(max)) SenderEmailUsage
  ,(select cast( sf.fConfigParam#Value('ShowLicensesToRegistrant')  as bit))                    ShowLicensesToRegistrant
  , cast( 'This configuration option shows or hides the logged in registrant''s current registration from the client portal.' as nvarchar(max)) ShowLicensesToRegistrantUsage
  ,(select cast( sf.fConfigParam#Value('ShowNoteLinkOptions')       as bit))                    ShowNoteLinkOptions
  , cast( 'This controls whether or not the advanced linking features for notes is available in the user interface.' as nvarchar(max)) ShowNoteLinkOptionsUsage
  ,(select cast( sf.fConfigParam#Value('SignupMessage')             as nvarchar(4000)))         SignupMessage
  , cast( 'This text is displayed to the applicant during sign up, this is informative text letting the applicant know what they are signing up for.' as nvarchar(max)) SignupMessageUsage
  ,(select cast( sf.fConfigParam#Value('SpecializationsReqOrgs')    as bit))                    SpecializationsReqOrgs
  , cast( 'This controls whether the Specializations assigned to members require that a granting organization be specified. Otherwise a granting organization is optional.' as nvarchar(max)) SpecializationsReqOrgsUsage
  ,(select cast( sf.fConfigParam#Value('SystemUser')                as nvarchar(75)))           SystemUser
  , cast( 'This value is applied to standard auditing columns ("created by", "updated by") when changes are made to the database by automated batch processes such as importing data from external sources. The value is not a licensed user account and cannot be used to login.' as nvarchar(max)) SystemUserUsage
  ,(select cast( sf.fConfigParam#Value('TechnicalHelpDeskNumber')   as nvarchar(50)))           TechnicalHelpDeskNumber
  , cast( 'The technical help desk number is displayed in the application to give users a number to call when they have technical issues.' as nvarchar(max)) TechnicalHelpDeskNumberUsage
  ,(select cast( sf.fConfigParam#Value('TenantReservationHours')    as int))                    TenantReservationHours
  , cast( 'This value controls how long (in hours) a pending trial request will wait for confirmation by the user.  During this period no other potential tenants can attempt to reserve the same sub-domain, tenant name etc. so that duplication is avoided.' as nvarchar(max)) TenantReservationHoursUsage
  ,(select cast( sf.fConfigParam#Value('TimeOut')                   as int))                    TimeOut
  , cast( 'The maximum number of minutes the application should be allowed to remain idle for external users before requiring another login.This is a security parameter that requires that the user to login again after the set period of inactivity. This prevents users from leaving logged workstations accessible for long periods of time to passers-by. Note that inactive time is measured as the gap between database accesses. Simple mouse movement is not sufficient to restart the idle-time clock.' as nvarchar(max)) TimeOutUsage
  ,(select cast( sf.fConfigParam#Value('TOSRegistrant')             as nvarchar(4000)))         TOSRegistrant
  , cast( 'This text is displayed to the registrant during sign up. The registrant must agree to these terms before they are able to use Alinity™.' as nvarchar(max)) TOSRegistrantUsage
  ,(select cast( sf.fConfigParam#Value('TrackEmailOpens')           as bit))                    TrackEmailOpens
  , cast( 'When set to on this will cause e-mail sent from the system to include a beacon image that will allow, in most cases, the ability to record when a particular e-mail was opened. Do note that many e-mail clients may refuse to open images or will strip off any tracking information before opening the image and therefore the system will be unable to determine if e-mails to those clients have been opened.' as nvarchar(max)) TrackEmailOpensUsage
  ,(select cast( sf.fConfigParam#Value('UnusedAccountWarningDays')  as int))                    UnusedAccountWarningDays
  , cast( 'The maximum number of days a user account can go without logging in, before it is identified for follow-up as a possible unused account.  A built-in task trigger (enabled by default) creates follow-up tasks for accounts that have been unused for this number of days.  Removing unused accounts from the system is a security best-practice.' as nvarchar(max)) UnusedAccountWarningDaysUsage
  ,(select cast( sf.fConfigParam#Value('UserProfileReviewMonths')   as int))                    UserProfileReviewMonths
  , cast( 'This value is used to identify User Accounts that require review.  User Accounts should be reviewed periodically to ensure they are still required and that access provided to the user remains appropriate.  The target interval between reviews is specified in months .  When an account has not been reviewed within the period defined, an icon appears to mark the record overdue and these accounts can also be accessed through a built-in query.' as nvarchar(max)) UserProfileReviewMonthsUsage
  ,(select cast( sf.fConfigParam#Value('ValidEmailDomains')         as varchar(50)))            ValidEmailDomains
  , cast( 'This value defines the list of email domains that will be considered valid by the application. This is a comma delimited list: e.g. ".com, .ca, .edu" (no quotes).  If a domain is entered on an email address that is not in the list, and that business rule is enabled, then an error is raised and the email address cannot be saved. The purpose of this value is to improve accuracy and reliability of email addresses managed in the application. To allow email addresses from any domain, either turn the business rule off in the configuration or set this value to "*" (no quotes).' as nvarchar(max)) ValidEmailDomainsUsage
  ,(select cast( sf.fConfigParam#Value('YearsInactiveLimit')        as int))                    YearsInactiveLimit
  , cast( 'This value controls the number of years a member on an Inactive Register (inactive status) will appear on the Public Directory. The limit is applied on Employer and Member directory features if configured. To include inactive members on the Directories forever, enter 99.' as nvarchar(max)) YearsInactiveLimitUsage
GO
EXEC sp_addextendedproperty N'MS_Description', N'This is an optional date that can be set to block changes to transactions in closed accounting periods. The application prevents changes to transactions on or before this date. Updating this date is normally only required after all GL transactions from Alnity for the year have been provided and reconciled in your external accounting system.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'AcctgTrxLockedDate'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value is used to store the email address of the system administrator. This is not the address of the products''s help desk but rather for end users to send requests to related to program administration and policy questions.  For example, to request a change in user rights granted, or to make a name change if such changes cannot be made directly by end users in the application. It is best practice to use a group email address rather than an individual email so that personnel changes do not require that the value be updated.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'AdministratorEmail'
GO
EXEC sp_addextendedproperty N'MS_Description', N'The maximum number of minutes the application should be allowed to remain idle for administrators before requiring another login.This is a security parameter that requires that the user to login again after the set period of inactivity. This prevents users from leaving logged workstations accessible for long periods of time to passers-by. Note that inactive time is measured as the gap between database accesses. Simple mouse movement is not sufficient to restart the idle-time clock.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'AdminTimeOut'
GO
EXEC sp_addextendedproperty N'MS_Description', N'The lowest number that should be generated for new applicants. This values sets the starting point for new applicant numbers to assign.  The value is NOT updated once application numbers start being assigned. The number range must be distinct from converted registration numbers and new Registration numbers if using both Application and Registration number sequences. Contact the Help Desk for assistance with setting this value.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'ApplicantNoMinimum'
GO
EXEC sp_addextendedproperty N'MS_Description', N'A template controlling the format of registrant numbers assigned to applicants. Example: A#### results in A1001, A1002, etc. Any letter prefix or suffix may be used with “#” indicating number of digits. The minimum number of digits is 4. To avoid assigning a number from a separate sequence to applicants, leave this value set at “[NONE]” and a Registration number is assigned immediately rather than only after they applicant achieves their first Active-Practice registration (recommended). If using both sequences, ensure they cannot duplicate. Contact the Help Desk for assistance with setting this value.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'ApplicantNoTemplate'
GO
EXEC sp_addextendedproperty N'MS_Description', N'The name given to the installed version of the application. This value cannot be updated through configuration.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'ApplicationName'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value controls how far into the past (in units of DAYS) a transaction, including security and other forms of record assignments, can be dated. In general back dating should be avoided, however, it may be required during conversion and start-up to load historical data.  In other situations it may be necessary to allow prior-day entries to be dated correctly. Always keep backdating intervals allowed as short as possible.  Back dating business rules can be turned off on selected tables during system conversion using the Business Rules Management screen.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'BackDatingLimit'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This configuration option controls whether or not a member can unlock their CCP form after it has been approved. This should be set to ON (1) if members are allowed to enter activities on their CCP form after their renewal or reinstatement has already been approved. With this setting ON the member can only unlock the form if they are currently within the CCP collection period (set on the Registration Schedule settings page). this setting does not apply for administrators unlocking the form.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'CCPUnlockAfterApprove'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value is required on exports to the Canadian Institute of Health Informatics (CIHI) to identify the type of profession or occupation regulated by your College. If you do not know this code you may be able to find it on the "Codes and Classification" section of the CIHI website:  https://www.cihi.ca/en/submit-data-and-view-standards/codes-and-classifications. Use a general search if the link is no longer current.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'CIHIOccupationID'
GO
EXEC sp_addextendedproperty N'MS_Description', N'The client''s mailing address used in reports or invoices. The mailing address must be in HTML format using break tags (br>) for new lines.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'ClientMailingAddress'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This text is displayed in the header for the client application on a desktop. This title will be seen by registrants and employers, normally this will be your organization name.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'ClientPortalHeaderTitle'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This text is displayed in the header for the client application on a mobile device. This title will be seen by registrants and employers, normally this will be your organization name. This name is shorter then the desktop name to prevent the title from wrapping to multiple lines.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'ClientPortalMobileTitle'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value is used to convert time values set at the database server with times provided by the end user. This option is important where the database server and client workstations are not in the same time zone. The value must be entered using a number of hours from the Universal Time Code/Greenwich Mean Time.  For example, Mountain Standard Time is "-06:00" from UTC/GMT (and -07:00 during the daylight savings period).', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'ClientTimeZoneOffset'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value is appended onto error messages where the source of the error appears to be configuration settings or missing setup values. The text should advise the user what to do in this scenario. Normally this will include updating the configuration or contacting the help desk if assistance is required. Since product help desk support is only provided to licensed administrators, it may be appropriate to include an email address and phone number for the local administrator. Note that a separate suffix is available for "program" errors.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'ConfigErrorSuffix'
GO
EXEC sp_addextendedproperty N'MS_Description', N'A notice advising readers of the exclusive right of Softworks Group Inc. to the intellectual property embedded in the application. This value cannot be updated through configuration.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'Copyright'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value is set by the application batch validation of data is performed.  If any errors are detected, the database icon presented in the user interface is set to a red color. Otherwise the icon appears in a non-highlighted color matching the rest of the screen design.  Note that the icon color is set automatically when batch validation is run and cannot be set manually through configuration.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'DatabaseStatusColor'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value identifies when the last set of model updates were applied to the database. The value is useful for debugging issues when working with the help desk and also for applying the correct set of change scripts for upgrades.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'DBModelNo'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This is the list of people with major roles in the design and development of Alinity™.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'DevelopmentTeam'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value is used to record whether the adjustment for day-light-savings time is currently active (ACTIVE) or not (INACTIVE), or, whether DST is not applied (N/A).  Changes to this value where DST is observed are managed automatically by the application. A process sets the value to "ON" each March and "OFF" each November.  Changing the value to "N/A" will stop adjustments to your time zone offset for DST.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'DSTStatus'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value controls whether or not search by registrant no is allowed on the employer directory.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'EMP_DIR_RegNoEnabled'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value is used to configure which filters are available on the employer directory. The filter types must be separated by a comma and is limited to only the filters configured in the product (eg: "PRACTICEREGISTER,EFFECTIVETIME"). Contact Softworks support for assistance when modifying this value.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'EmployerDirectoryFilters'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This controls whether or not split payments by members is allowed.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'EnableSplitPayments'
GO
EXEC sp_addextendedproperty N'MS_Description', N'When 1 (ON) indicates that employment-supervisor relationships require the supervisor to be a member on an eligible Practice Register and working at the same organization.  When 0 (OFF) any person can be designated as a supervisor.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'EnforceMemberSupervisors'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value controls whether Saturday and Sunday will be excluded when calculating whether a record has been "recently accessed" according to hour limit put into the Recent-Access-Threshold. For example, if the threshold is set at 24 hours and this parameter is turned on (1), then a record accessed Friday afternoon will still be considered to be recently accessed the following Monday morning.  Note that this configuration value is always treated as OFF (0) if the recent hours access threshold is set to any value greater than 120 hours.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'ExcludeWeekendsForRecent'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value controls how far into the future (in units of DAYS) a transaction, including security and other forms of record assignments, can be dated.  Allowing some future dating is convenient where there is a need to setup a record to apply automatically in the future (for example setting up grants on a user record to take effect next month). Setting a limit on the extent of future dating helps prevent data entry errors.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'FutureDatingLimit'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value is used to identify how long the same password can be used before requiring changing. The maximum interval is specified in months.  When a glass break password has not been changed within the period defined, the user is forced to change their password on the next login. Changing passwords periodically is considered best practice for security. ', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'GlassBreakPwdChangeMonths'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This is the email address for submitting tickets to the Help Desk. When a program error is encountered, details are automatically captured by the software and the user is given the option to send them to the help desk. This is the email address used.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'HelpdeskEmail'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value is used to identify when a invitation expires. When a user signs up through the website they receive an email asking them to confirm their account. After the invitation is sent, the email address is reserved as a unique user name which no else can try to register while it is reserved. This value controls how long the email address is reserved for. The setting is in hours.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'InviteReservationHours'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value indicates whether insurance information is required/collected for practicing members.  When turned off (set to "0") insurance features are removed from the user interface.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'IsInsuranceEnabled'
GO
EXEC sp_addextendedproperty N'MS_Description', N'This value indicates whether exam information is displayed to members on the portal. When turned off (set to "0") the "My Exams" feature does not display.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'IsMyExamsEnabled'
GO
EXEC sp_addextendedproperty N'MS_Description', N'The maximum number of months the history of background jobs should be retained. One (1) month is recommended and 3 months is the maximum.', 'SCHEMA', N'dbo', 'VIEW', N'vConfigParam#Active', 'COLUMN', N'JobHistoryRetentionMonths'
GO
